head.script:
const gaId = '';
if (gaId.length !== 0) {
    window.dataLayer = window.dataLayer || [];

    function gtag() {
        dataLayer.push(arguments);
    }

    gtag('js', new Date());

    gtag('config', gaId);
}

const ga4Id = '';
if (ga4Id.length !== 0) {
    window.dataLayer = window.dataLayer || [];

    function gtag() {
        dataLayer.push(arguments);
    }

    gtag('js', new Date());

    gtag('config', ga4Id);
}
